    def write_number(self, number):
        if self.result.text() == '0' or self.reset:
            self.result.setText(number)
            self.reset = False
        else:
            self.result.setText(self.result.text() + number)

    def get_result(self):
        try:
            res = eval(self.result.text())
            self.reset = True
            self.result.setText(str(res))

        except SyntaxError:
            res = 'incorrect arguments'
            error = QMessageBox()
            error.setWindowTitle('Error')
            error.setText(res)
            error.setIcon(QMessageBox.Warning)
            error.setStandardButtons(QMessageBox.Reset | QMessageBox.Ok)
            error.buttonClicked.connect(self.pop_up_actions)
            error.exec_()

    def pop_up_actions(self, btn):
        print(btn.text())
        if btn.text() == 'Reset':
            self.result.setText('0')
            self.reset = False


